import React, { useState } from 'react';
import { View, Text, Button } from 'react-native';

const ReactApp = () => {
  const [counter, setCounter] = useState(0);
  return (
    <View>
      <Text>
        {counter}
      </Text>
      <Button
        onPress = {() => setCounter(counter + 1)}
        title = '+'
      />
      <Button
        onPress = {() => setCounter(counter - 1)}
        title = '-'
      />
    </View>
  )
}

export default ReactApp;
