import React, { useState } from 'react';
import { View, Text } from 'react-native';

const ReactApp = () => {
  var counter = 0;

  const [myText, setMyText] = useState(counter);
  return (
    <View>
      <Text onPress = {() => {
        counter++;
        setMyText(counter);
      }}>
        {myText}
      </Text>
    </View>
  )
}

export default ReactApp;
